import axios from 'axios';
import React from 'react';
import './Login.css';
export default function Login() {

  var [dat,setdat] = useState({
    email:'',
    pass:''
  });

  const setemail = (e)=>{
    setdat({
      email:e.target.value,
      pass:data.pass,
      
    })
  }
  const setpass = (e)=>{
    setdat({
      email:data.email,
      pass:e.target.value,
    
    })
  }

  const login  = ()=>{

      axios.post('http://localhost:5000/login',dat).then((response)=>{
          console.log(response);
    })

  }

  return (
    <div className='sign'>

        <div className="header">Login</div>
        <div className="inp">
        <input type="email" name='email' placeholder='Email-Id' className='text' value={dat.email} onChange={setemail}/>
            <input type="password"  name='pass' placeholder='Password' className='text' value={dat.pass} onChange={setpass}/>
  
        </div>
        <button className='btn' onClick={login}>Submit</button>
      
    </div>
  )
}
